import React from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  const [formData, setFormData] = React.useState({
    sourceSystem: "",
    targetSystem: "",
    scenarioName: "",
  });

  const navigate = useNavigate();

  const handleChange = ({ target }) => {
    const { name, value } = target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    navigate("/configure", { state: formData });
  };

  return (
    <div>
        <h1>Integration tool</h1>
        <form onSubmit={handleSubmit}>
            <input type="text" name="sourceSystem" placeholder="Source System" onChange={handleChange} />
            <input type="text" name="targetSystem" placeholder="Target System" onChange={handleChange} />
            <input type="text" name="scenarioName" placeholder="Scenario Name" onChange={handleChange} />
            <button type="submit">Create Flow</button>
        </form>
    </div>
  );

}


export default Home;
