import React from "react";
import { useLocation, Link } from "react-router-dom";
import axios from "axios";
import "./Review.css";

function Review() {
  const location = useLocation();
  const data = location.state || {};
  const [message, setMessage] = React.useState(""); // State to manage success or error message

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      console.log("Data to be saved to the server", data);
      const response = await axios.post(
        "http://localhost:8080/api/configurations",
        data
      );
      if (response.status === 200) {
        console.log("Success:", response.data);
        setMessage("Configuration submitted successfully!");
      } else {
        setMessage("Unexpected response from the server.");
      }

    } catch (error) {
      console.error(error);
      setMessage("Failed to submit configuration. Please try again."); // Set error message
    }
  };

  return (
    <div className="review-container">
      <h1>Review Configurations</h1>
      <div className="review-data">
        <pre>{JSON.stringify(data, null, 2)}</pre>
      </div>
      <button className="review-submit-btn" onClick={handleSubmit}>
        Submit
      </button>
      {message && <p className="review-message">{message}</p>}{" "}
      <Link to="/" className="review-home-link">
        Go Back to Home
      </Link>
      {/* Display message */}
    </div>
  );
}

export default Review;
