import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

function Configure() {
  const location = useLocation();
  const navigate = useNavigate();
  const initialData = location.state || {};

  const [configData, setConfigData] = React.seState({
    inputFolder: "",
    outputFolder: "",
    inputQueue: "",
    outputQueue: "",
  });

  const handleChange = ({ target }) => {
    const { name, value } = target;
    setConfigData({ ...configData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    navigate("/Review", { state: { ...initialData, ...configData } });
  };

  return (
    <div>
      <h1>Configure Use Case</h1>
      <form onSubmit={handleSubmit}>
        <label>Input folder: </label>
        <input
          type="text"
          name="inputFolder"
          value={configData.inputFolder}
          onChange={handleChange}
          required
        />
        <label>Output folder: </label>
        <input
          type="text"
          name="outputFolder"
          value={configData.outputFolder}
          onChange={handleChange}
          required
        />
        <label> Input Queue</label>
        <input
          type="text"
          name="inputQueue"
          value={configData.inputQueue}
          onChange={handleChange}
          required
        />
        <label>Output Queue</label>
        <input
          type="text"
          name="outputQueue"
          value={configData.outputQueue}
          onChange={handleChange}
          required
        />
      </form>
    </div>
  );
}

export default Configure;
