import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./Configure.css";

function Configure() {
  const location = useLocation();
  const navigate = useNavigate();
  const initialData = location.state || {};

  // State for configuration data
  const [configData, setConfigData] = React.useState({
    workflowType: "folder-to-folder", // Default type
    inputFolder: "",
    outputFolder: "",
    inputQueue: "",
    outputQueue: "",
  });

  const handleChange = ({ target }) => {
    const { name, value } = target;
    setConfigData({ ...configData, [name]: value });
  };

  const handleWorkflowTypeChange = (event) => {
    const workflowType = event.target.value;
    setConfigData({
      workflowType,
      inputFolder: "",
      outputFolder: "",
      inputQueue: "",
      outputQueue: "",
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    navigate("/Review", { state: { ...initialData, ...configData } });
  };

  return (
    <div className="configure-container">
      <h1>Configure Use Case</h1>
      <form className="configure-form" onSubmit={handleSubmit}>
        <label>
          Workflow Type:
          <select
            name="workflowType"
            value={configData.workflowType}
            onChange={handleWorkflowTypeChange}
            required
          >
            <option value="folder-to-folder">Folder to Folder</option>
            <option value="folder-to-queue">File to Queue</option>
          </select>
        </label>

        {configData.workflowType === "folder-to-folder" && (
          <>
            <label>
              Input Folder:
              <input
                type="text"
                name="inputFolder"
                value={configData.inputFolder}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Output Folder:
              <input
                type="text"
                name="outputFolder"
                value={configData.outputFolder}
                onChange={handleChange}
                required
              />
            </label>
          </>
        )}

        {configData.workflowType === "folder-to-queue" && (
          <>
            <label>
              Input Folder:
              <input
                type="text"
                name="inputFolder"
                value={configData.inputFolder}
                onChange={handleChange}
                required
              />
            </label>
            <label>
              Output Queue:
              <input
                type="text"
                name="outputQueue"
                value={configData.outputQueue}
                onChange={handleChange}
                required
              />
            </label>
          </>
        )}

        <button type="submit">Review</button>
      </form>
    </div>
  );
}

export default Configure;
